  return (
    <ul>
      {sizes.map((size, index) => (
        <li
          key={index}
          onClick={() => onSyzeActive(index)}
          className={activeSyze === index ? "active" : ""}
        >
          {size} Ð».
        </li>
      ))}
    </ul>
  );
};
export default SizePizza;
